const W: usize = INPUT[0].len();

fn in_slope(dh: usize, dw: usize) -> usize {
  INPUT.iter()
    .step_by(dh)
    .zip((0..W).cycle().step_by(dw))
    .skip(1)
    .filter(|&(row, c)| row[c] == b'#')
    .count()
}

aoc2020::main! {
  let part_one = in_slope(1,3);
  let part_two = [(1,1),(1,3),(1,5),(1,7),(2,1)].iter()
    .map(|&(dh,dw)| in_slope(dh,dw))
    .product::<usize>();
  (part_one, part_two)
}
