#[derive(Clone,Copy)]
enum Op {
  Mem(usize, usize),
  Mask(&'static [u8]),
}

fn part_one() -> usize {
  let mut mem = HashMap::new();
  let mut mask: &[u8] = b"";
  for &op in &INPUT {
    match op {
      Op::Mem(addr, mut v) => {
        for (i,c) in mask.iter().rev().enumerate() {
          match c {
            b'0' => v &= !(1 << i),
            b'1' => v |= 1 << i,
            _    => {}
          }
        }
        mem.insert(addr, v);
      }
      Op::Mask(m) => mask = m,
    }
  }
  mem.values().sum()
}

fn write(
  mem: &mut HashMap<usize, usize>,
  mask: &[u8],
  v: usize,
  i: usize,
  addr: usize,
) {
  let bit = 1 << (35 - i);
  match mask.get(i) {
    Some(b'0') => write(mem, mask, v, i+1, addr),
    Some(b'1') => write(mem, mask, v, i+1, addr | bit),
    Some(b'X') => {
      write(mem, mask, v, i+1, addr);
      write(mem, mask, v, i+1, addr ^ bit);
    }
    _ => { mem.insert(addr,v); }
  }
}

fn part_two() -> usize {
  let mut mem = HashMap::new();
  let mut mask: &[u8] = b"";
  for &op in &INPUT {
    match op {
      Op::Mem(addr, v) => write(&mut mem, mask, v, 0, addr),
      Op::Mask(m) => mask = m,
    }
  }
  mem.values().sum()
}

aoc2020::main! {
  (part_one(), part_two())
}
